import { render } from "react-dom";

class PictureBar extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      imageList: [
        "https://cdn.homedics.com/media/catalog/product/cache/1/thumbnail/1000x/17f82f742ffe127f42dca9de82fb58b1/m/s/msk-f1.jpg",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSFlHhVX_6NV02EnmlsUSzNB2LBoRhLiX7gtQ&usqp=CAU",
        "https://www.supermarketnews.com/sites/supermarketnews.com/files/styles/article_featured_retina/public/Coronavirus-empty-shelves.png?itok=ec7giIOK",
        "https://www.nicepng.com/png/detail/118-1186281_wash-hands-png-wash-your-hands-clip-art.png",
        "https://www.nice.com/png/detail/118-1186281_wash-hands-png-wash-your-hands-clip.png",
      ],

      index: 0,
    };
  }

  componentDidMount() {
    this.myTimer();
    // this.checkImage(
    //     this.state.imageList[4],
    //     function () {
    //       console.log("pic is good.....");
    //     },
    //     function () {
    //       console.log("pic is bad....");
    //     }
    //   );
  }

  myTimer = () => {
    if (this.state.index > 5) {
      console.log("picture index is over 10");
      this.setState({ index: 0 });
    }

    this.checkImage(this.state.imageList[4], 
        this.myTimeoutMethod(), 
        this.increaseIndex()
        );

    // setTimeout(
    //   function () {
    //     this.setState({ index: this.state.index + 1 });
    //     console.log("picture index after increase");
    //     console.log(this.state.index);

    //     this.myTimer();
    //   }.bind(this),
    //   5000
    // );
  };

  increaseIndex = () => {
    console.log("pic is bad....");
    this.setState({ index: this.state.index + 1 });
    console.log("picture index after increase in the new method!!!!");
    console.log(this.state.index);
  }

  checkImage = (imageSrc, good, bad) => {
    var img = new Image();
    img.onload = good;
    img.onerror = bad;
    img.src = imageSrc;
  };

  myTimeoutMethod = () => {
    setTimeout(
      function () {
        this.setState({ index: this.state.index + 1 });
        console.log("picture index after increase");
        console.log(this.state.index);

        this.myTimer();
      }.bind(this),
      5000
    );
  };

  render() {
    return (
      <div>
        <div>image div</div>
        {/* <img style={{height: "200px", width:"200px"}} src={this.state.imageList[this.state.index]}/> */}
        <img
          style={{ height: "200px", width: "200px" }}
          src={this.state.imageList[this.state.index]}
        />
      </div>
    );
  }
}

export default PictureBar;
