import { render } from "react-dom";

class PictureBar extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            imageList : [
                "https://cdn.homedics.com/media/catalog/product/cache/1/thumbnail/1000x/17f82f742ffe127f42dca9de82fb58b1/m/s/msk-f1.jpg"
            ],
            index: 0
        }
    }



    componentDidMount() {
        this.myTimer();
       }
   
       myTimer = () => {
               if ( this.state.index > 11 ) {
                   console.log("picture index is over 10")
                   this.setState({index:0})
               }
   
   
           setTimeout(
               function() {
                   this.setState({index: this.state.index + 1});
                   console.log("picture index after increase")
                   console.log(this.state.index)
                   this.myTimer();
               }.bind(this), 5000)
       }
   

render() {

    return (
        <div>
            <div>image div</div>
            <img style={{height: "200px", width:"200px"}} src={this.state.imageList[0]}/>
        </div>
    )
}

}


export default PictureBa
