class Prices extends React.Component {
  state = {
    currency: "USD",
  };

  render() {
    let list = "";

    if (this.state.currency === "USD") {
        list = (
        <li className="list-group-item">
          Bitcoin rate for {this.props.bpi.bpi.USD.description}:{" "}
          <span class="badge badge-primary"> {this.props.bpi.bpi.code}</span>
          <strong>{this.props.bpi.bpi.USD.rate}</strong>
        </li>
      );
    } else if (this.state.currency === "GBP") {
        list = (
      <li className="list-group-item">
        Bitcoin rate for {this.props.bpi.bpi.GBP.description}:{" "}
        <span class="badge badge-primary">{this.props.bpi.bpi.GBP.code}</span>
        <strong> {this.props.bpi.bpi.GBP.rate}</strong>
      </li>
        )
    } else if (this.state.currency === "EUR") {
        list = (
      <li className="list-group-item">
        Bitcoin rate for {this.props.bpi.bpi.EUR.description}:{" "}
        <span class="badge badge-primary">{this.props.bpi.bpi.EUR.code}</span>
        <strong> {this.props.bpi.bpi.EUR.rate}</strong>
      </li>
        )
    }

    console.log(this.props.bpi.bpi.USD);
    return (
      <div>
        <ul className="list-group">{list}</ul>
      </div>
    );
    console.log(this.props);
  }
}

export default Prices;
